import React, { useState } from "react";

function App() {
  const [number, setNumber] = useState(0);

  return (
    <div>
      {number}
      {/* 익명함수 */}
      <button
        onClick={() => {
          setNumber(number + 1);
        }}
      >
        {" "}
        +{" "}
      </button>
      <button
        onClick={() => {
          setNumber(number - 1);
        }}
      >
        -{" "}
      </button>
    </div>
  );
}

export default App;
