        ÏÇ≠Ï†úÌïòÍ∏∞
      </CustomButton>
    </div>
  );
}

const App = () => {
  const [users, setUsers] = useState([
    { id: 1, age: 30, name: "ÏÜ°Ï§ëÍ∏∞" },
    { id: 2, age: 24, name: "ÏÜ°Í∞ï" },
    { id: 3, age: 21, name: "ÍπÄÏú†Ï†ï" },
    { id: 4, age: 29, name: "Íµ¨ÍµêÌôò" },
  ]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");

  const addUserHandler = () => {
    console.log("Ìï®Ïàò ÏûëÎèôÏùÄ Ìï¥?");

    const newUser = {
      id: users.length + 1,
      age: age,
      name: name,
    };
    console.log("newUser:", newUser);

    // Í∏∞Ï°¥Î∞∞Ïó¥ÏùÑ Î®ºÏ†Ä Ïù¥Î†áÍ≤å ÎÑ£Í≥†, ÏÉàÎ°úÏö¥ userÎ•º Ï∂îÍ∞ÄÌïú Í≤É
    setUsers([...users, newUser]);
    console.log("Ï∂îÍ∞ÄÎêú UsersÎäî?", users);
  };

  const deleteUserHandler = (id) => {
    // user.idÍ∞Ä ÎÇ¥Í∞Ä ÏÑ§Ï†ïÌïú idÏôÄ Í∞ôÏßÄ ÏïäÏúºÎ©¥, Í∑∏Í≤ÉÎßå filterÌï¥ÏÑú ÏÉàÎ°úÏö¥ Ïú†Ï†ÄÎ¶¨Ïä§Ìä∏Î•º ÎßåÎì§Ïñ¥Îùº
    const newUserList = users.filter((user) => user.id !== id);
