  return (
    <div>
      <div className="app-style">
        {/* usersÏùò Ï†ïÎ≥¥Îì§ÏùÑ mapÏù¥ ÎèåÎ©¥ÏÑú, users ÏïàÏùò Ï†ïÎ≥¥Îì§ÏùÑ ÎÑòÍ≤®Ï£ºÍ≥† ÏûàÏóàÍ≥† */}
        {users.map((a) => {
          console.log("mapÏù¥ ÎΩëÏïÑÏò®Í±¥ ÌîÑÎ°úÌçºÌã∞(ÏòµÏ†ùÏù¥Îã§) a : ", a);
          console.log("a.idÎäî Î≠êÏïº?", a.id);
          return (
            <User
              ÌîÑÎ°≠Ïä§Î°úÎÑòÍ∏∏delete={deleteUserHandler}
              userÏóêÎÑòÍ∏¥Í∞í={a}
              Ïó¨Í∏∞ÎèÑÏù¥Î¶ÑÎ∞îÍøîÎèÑÎêòÏßÄ={a.id}
