import { Item } from "../types/type";

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Ïï°ÏÖò Í∞ùÏ≤¥ ÎßåÎì§Îïå, Ïù¥Í±¥ Î¨¥Ï°∞Í±¥ const ÌÉÄÏûÖÏù¥Îã§.
const ADD = "itemReducer/ADD" as const;
const DELETE = "itemReducer/DELETE" as const;
const CLEAR = "itemReducer/CLEAR" as const;
const EDIT = "itemReducer/EDIT" as const;

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ Í∞ÅÍ∞Å string,string,booleanÌòïÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const addItem = (id: string, name: string, clear: boolean) => ({
  type: ADD,
  payload: { id, name, clear },
});

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ stringÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const deleteItem = (id: string) => ({
  type: DELETE,
  payload: id,
});

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ stringÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const clearItem = (id: string) => ({
  type: CLEAR,
  payload: id,
});

export const editItem = (
  id: string,
  name: string,
  clear: boolean,
  edit: boolean
) => ({
  type: EDIT,
  payload: { id, name, clear, edit },
});

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// ÌÉÄÏûÖÏóòÎ¶¨Ïñ¥Ïä§Î•º ÎßåÎì§Ïñ¥Ï§ÄÍ±¥Îç∞,
// Îì§Ïñ¥Ïò§Îäî ActionÏùò ÌÉÄÏûÖÏùÄ addItemÏù¥Í±∞ÎÇò deleteItemÏù¥Í±∞ÎÇò clearItemÏù¥Îã§.
type TodoAction =
  | ReturnType<typeof addItem>
  | ReturnType<typeof deleteItem>
  | ReturnType<typeof clearItem>
  | ReturnType<typeof editItem>;

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// ÌÉÄÏûÖÏóòÎ¶¨Ïñ¥Ïä§Î•º ÎßåÎì§Ïñ¥Ï§ÑÍ±¥Îç∞,
// TodoState(Ïù¥ÎãàÏÖúÌà¨Îëê)Ïùò
// todoÎùºÎäîÍ≤å ÏûàÏùÑÍ±¥Îç∞, Í∑∏ todoÎäî ItemÏù¥ÎùºÎäî ÌÉÄÏûÖÏùÑ Í∞ÄÏßÄÍ≥† ÏûàÏñ¥
type TodoState = {
  todo: Item[];
};

const initialTodo = {
  todo: [],
};

const itemReducer = (
  // TodoStateÏùò ÌÉÄÏûÖ => todo: Item[];
  // Item => itemId, Name,Clear ÌÉÄÏûÖÏù¥ ÏßÄÏ†ïÎêòÏñ¥ÏûàÏñ¥
  state: TodoState = initialTodo, //todo: []
  action: TodoAction
): TodoState => {
  switch (action.type) {
    case ADD:
      return {
        todo: state.todo.concat({
          itemId: action.payload.id,
          itemName: action.payload.name,
          clear: action.payload.clear,
        }),
      };
    case DELETE:
      return {
        todo: state.todo.filter((todo) => {
          return todo.itemId !== action.payload;
        }),
      };

    case CLEAR:
      return {
        todo: state.todo.map((todo) => {
          // todo.itemIdÏôÄ action.payloadÍ∞Ä Í∞ôÏúºÎ©¥ Ìï¥Îãπ todoÏóêÎã§Í∞Ä Î∞òÎåÄÍ∞íÏùÑ Îí§ÏßëÏñ¥Ï§òÎùº
          // Í∑∏Î†áÏßÄ ÏïäÏúºÎ©¥ Î≥ÄÌôîÏóÜÏù¥ todo Î∞òÌôò
          return todo.itemId === action.payload
            ? { ...todo, clear: !todo.clear }
            : todo;
        }),
      };

    case EDIT:
      return {
        todo: state.todo.map((todo) => {
          return todo.itemId === action.payload.id
            ? { ...todo, itemName: action.payload.name, edit: true }
            : todo;
        }),
      };

    default:
      return state;
  }
};

export default itemReducer;
