import { Item } from "../types/type";

// 👀 타입지정
// 액션 객체 만들때, 이건 무조건 const 타입이다.
const ADD = "itemReducer/ADD" as const;
const DELETE = "itemReducer/DELETE" as const;
const CLEAR = "itemReducer/CLEAR" as const;

// 👀 타입지정
// 들어오는 액션의 타입이 각각 string,string,boolean형이다 라고 타입지정
export const addItem = (id: string, name: string, clear: boolean) => ({
  type: ADD,
  payload: { id, name, clear },
});

// 👀 타입지정
// 들어오는 액션의 타입이 string이다 라고 타입지정
export const deleteItem = (id: string) => ({
  type: DELETE,
  payload: id,
});

// 👀 타입지정
// 들어오는 액션의 타입이 string이다 라고 타입지정
export const clearItem = (id: string) => ({
  type: CLEAR,
  payload: id,
});

// 👀 타입지정
// 타입엘리어스를 만들어준건데,
// 들어오는 Action의 타입은 addItem이거나 deleteItem이거나 clearItem이다.
type TodoAction =
  | ReturnType<typeof addItem>
  | ReturnType<typeof deleteItem>
  | ReturnType<typeof clearItem>;

// 👀 타입지정
// 타입엘리어스를 만들어줄건데,
// TodoState(이니셜투두)의
// todo라는게 있을건데, 그 todo는 Item이라는 타입을 가지고 있어
type TodoState = {
  todo: Item[];
};

const initialTodo = {
  todo: [],
};

const itemReducer = (
  // TodoState의 타입 => todo: Item[];
  // Item => itemId, Name,Clear 타입이 지정되어있어
  state: TodoState = initialTodo, //todo: []
  action: TodoAction
): TodoState => {
  switch (action.type) {
    case ADD:
      return {
        todo: state.todo.concat({
          itemId: action.payload.id,
          itemName: action.payload.name,
          clear: action.payload.clear,
        }),
      };
    case DELETE:
      return {
        todo: state.todo.filter((todo) => {
          return todo.itemId !== action.payload;
        }),
      };

    case CLEAR:
      return {
        todo: state.todo.map((todo) => {
          // todo.itemId와 action.payload가 같으면 해당 todo에다가 반대값을 뒤집어줘라
          // 그렇지 않으면 변화없이 todo 반환
          return todo.itemId === action.payload
            ? { ...todo, clear: !todo.clear }
            : todo;
        }),
      };

    default:
      return state;
  }
};

export default itemReducer;
