import { Item } from "../types/type";
import { RootState } from "../modules";
import { useSelector, useDispatch } from "react-redux";
import { deleteItem, clearItem, editItem } from "../modules/itemReducer";
import { useState } from "react";

const ItemList = () => {
  const [newEditName, setNewEditName] = useState("");
  const todo = useSelector((state: RootState) => state.itemReducer.todo);
  const dispatch = useDispatch();

  const clickDelete = (data: Item) => {
    dispatch(deleteItem(data.itemId));
  };

  const clickComplete = (data: Item) => {
    dispatch(clearItem(data.itemId));
  };

  const eidtItemName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setNewEditName(e.target.value);
  };

  const 딸깍 = (data: Item) => {
    const newComment: Item = {
      itemId: data.itemId,
      itemName: newEditName,
      clear: data.clear,
      edit: false,
    };
    dispatch(editItem(newComment));
  };

  return (
    <>
      <h3> 여기는 do</h3>
      <ul>
        {todo.map((data: Item) => {
          if (data.clear === true) {
            return (
              <li className="list-item" key={data.itemId}>
                <p>{data.itemName}</p>
                <div>
                  <button
                    className="button-delete"
                    onClick={() => clickDelete(data)}
                  >{`삭제`}</button>
                  <button
                    className="button-complete"
                    onClick={() => clickComplete(data)}
                  >
                    {data.clear ? "진행중" : "완료"}
                  </button>
                  <button style={{ margin: 50 }} onClick={() => 딸깍(data)}>
                    수정
                  </button>
                  <input
                    className="task-input"
                    value={newEditName}
                    onChange={eidtItemName}
                    type="text"
                  />
                </div>
              </li>
            );
          }
        })}
      </ul>

      <div>
        <h3>여기는 완료</h3>
        {todo.map((data: Item) => {
          if (data.clear === false) {
            return (
              <li className="list-item" key={data.itemId}>
                <p>{data.itemName}</p>
                <div>
                  <button
                    className="button-delete"
                    onClick={() => clickDelete(data)}
                  >{`삭제`}</button>
                  <button
                    className="button-complete"
                    onClick={() => clickComplete(data)}
                  >
                    {data.clear ? "진행중" : "완료"}
                  </button>
                </div>
              </li>
            );
          }
        })}
      </div>
    </>
  );
};

export default ItemList;
