import { useState } from "react";
import { v4 as uuid } from "uuid";
import { addItem } from "../modules/itemReducer";
import { useDispatch } from "react-redux";

const InsertItem = () => {
  //input value 스테이트
  const [itemName, setItemName] = useState("");
  const dispatch = useDispatch();

  // input에 변화가 있을때마다 실행되는 함수
  // 👀 타입지정
  // 여기서 이벤트는 ChangeEvent이고, HTMLInputElement이다.
  const changeItemName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setItemName(e.target.value);
  };

  //'추가하기'버튼 클릭시 실행되는 함수
  // 👀 타입지정
  // 여기서 이벤트는 MouseEvent이고 HTMLButtonElement이다.
  const clickButton = (e: React.MouseEvent<HTMLButtonElement>) => {
    const itemId = uuid();
    // dispatch를 통해 reducer의 addItem한테 3가지 액션을 보낼건데
    dispatch(addItem(itemId, itemName, false));
    setItemName("");
  };

  return (
    <>
      <input
        className="task-input"
        value={itemName}
        onChange={changeItemName}
        type="text"
      />
      <button className="button-add" onClick={clickButton}>{`생성`}</button>
    </>
  );
};

export default InsertItem;
