import { Item } from "../types/type";

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Ïï°ÏÖò Í∞ùÏ≤¥ ÎßåÎì§Îïå, Ïù¥Í±¥ Î¨¥Ï°∞Í±¥ const ÌÉÄÏûÖÏù¥Îã§.
const ADD = "itemReducer/ADD" as const;
const DELETE = "itemReducer/DELETE" as const;
const CLEAR = "itemReducer/CLEAR" as const;

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ Í∞ÅÍ∞Å string,string,booleanÌòïÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const addItem = (id: string, name: string, clear: boolean) => ({
  type: ADD,
  payload: { id, name, clear },
});

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ stringÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const deleteItem = (id: string) => ({
  type: DELETE,
  payload: id,
});

// üëÄ ÌÉÄÏûÖÏßÄÏ†ï
// Îì§Ïñ¥Ïò§Îäî Ïï°ÏÖòÏùò ÌÉÄÏûÖÏù¥ stringÏù¥Îã§ ÎùºÍ≥† ÌÉÄÏûÖÏßÄÏ†ï
export const clearItem = (id: string) => ({
  type: CLEAR,
  payload: id,
});

// ÌÉÄÏûÖÏóòÎ¶¨Ïñ¥Ïä§Î•º ÎßåÎì§Ïñ¥Ï§ÄÍ±¥Îç∞,
// Îì§Ïñ¥Ïò§Îäî ActionÏùò ÌÉÄÏûÖÏùÄ addItemÏù¥Í±∞ÎÇò deleteItemÏù¥Í±∞ÎÇò clearItemÏù¥Îã§.
type TodoAction =
  | ReturnType<typeof addItem>
  | ReturnType<typeof deleteItem>
  | ReturnType<typeof clearItem>;

type TodoState = {
  todo: Item[];
};

const initialTodo = {
  todo: [],
};

const itemReducer = (
  state: TodoState = initialTodo,
  action: TodoAction
): TodoState => {
  switch (action.type) {
    case ADD:
      return {
        todo: state.todo.concat({
          itemId: action.payload.id,
          itemName: action.payload.name,
          clear: action.payload.clear,
        }),
      };
    case DELETE:
      return {
        todo: state.todo.filter((todo) => {
          return todo.itemId === action.payload ? false : true;
        }),
      };

    case CLEAR:
      return {
        todo: state.todo.map((todo) => {
          return todo.itemId === action.payload
            ? { ...todo, clear: !todo.clear }
            : todo;
        }),
      };

    default:
      return state;
  }
};

export default itemReducer;
